const express = require('express');
const Artist = require('./models/Artist');
const router = express.Router();


// Create a new artist
router.post('/artists', async (req, res) => {
    try {
        const artist = new Artist(req.body);
        await artist.save();
        res.status(201).send(artist);
    } catch (error) {
        res.status(400).send(error.message);
    }
});

// Get all artists
router.get('/artists', async (req, res) => {
    try {
        const artists = await Artist.find();
        res.status(200).send(artists);
    } catch (error) {
        res.status(500).send(error.message);
    }
});

// Update an artist
router.put('/artists/:id', async (req, res) => {
    try {
        const artist = await Artist.findByIdAndUpdate(req.params.id, req.body, { new: true, runValidators: true });
        if (!artist) {
            return res.status(404).send('Artist not found');
        }
        res.status(200).send(artist);
    } catch (error) {
        res.status(400).send(error.message);
    }
});

// Delete an artist
router.delete('/artists/:id', async (req, res) => {
    try {
        const artist = await Artist.findByIdAndDelete(req.params.id);
        if (!artist) {
            return res.status(404).send('Artist not found');
        }
        res.status(200).send('Artist deleted');
    } catch (error) {
        res.status(500).send(error.message);
    }
});

router.get('/artists/paginate', async (req, res) => {
    const { limit = 10, offset = 0 } = req.query;
    try {
        const artists = await Artist.find().skip(parseInt(offset)).limit(parseInt(limit));
        res.status(200).send(artists);
    } catch (error) {
        res.status(500).send(error.message);
    }
});

module.exports = router;


